import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'iban',
    standalone: true
})
export class IbanPipe implements PipeTransform {

  transform(value: unknown, ...args: unknown[]): unknown {
    if (typeof value !== 'string') {
      return value;
    }
    return value.replace(/(.{4})/g, '$1 ').trim();
  }

}

