export interface CategoryAndAmount {
    amount: number;
    category: string;
    categoryId: number;
    isRevenue: boolean;

}

export function anyIsUndefinedOrEmpty(...args: any[]): boolean {
    for (var a of args) {
        if (a === undefined || a === null) {
            return true;
        }
        //check if string
        if (typeof a === 'string' && a === "") {
            return true;
        }


    }

    return false;

}

