    transaction: string | null;
    currencyDate: Date | null;
    amount: Number | null;
    currency: string | null;
    bic: string | null;
    countryCode: string | null;
    communications: string | null;
    category: string | null;
    manuallyAssignedCategory: Boolean | null;
    isRecurring: Boolean | null;
