  expandable!: boolean;
  name!: string;
  qualifiedName!: string;
  budgetTreeNodeId!: number;
  budgetTreeNodeAmount!: number;
  budgetTreeNodeParentId!: number;
}



export interface FindOrCreateBudgetResponse {
