import {SelectionModel} from '@angular/cdk/collections';
import {FlatTreeControl} from '@angular/cdk/tree';
import {Component, EventEmitter, Input, OnInit, Output, ViewChild} from '@angular/core';
import {
  MatTree,
  MatTreeFlatDataSource,
  MatTreeFlattener,
  MatTreeNode,
  MatTreeNodeDef,
  MatTreeNodePadding,
  MatTreeNodeToggle
} from '@angular/material/tree';
import {BehaviorSubject} from 'rxjs';
import {AppService} from '../app.service';
import {FormBuilder, FormGroup, FormsModule, ReactiveFormsModule} from "@angular/forms";
import {MatFormField} from "@angular/material/form-field";
import {MatAutocomplete, MatAutocompleteTrigger} from "@angular/material/autocomplete";
import {CategoryNode, FlatCategoryNode, NO_CATEGORY} from "../model";
import {MatInput} from '@angular/material/input';
import {MatOption} from '@angular/material/core';
import {MatIconButton} from '@angular/material/button';
import {MatCheckbox} from '@angular/material/checkbox';
import {MatIcon} from '@angular/material/icon';
import {TransactionTypeEnum} from "@daanvdn/budget-assistant-client";


// @Injectable({ providedIn: "root" })
export class BackingDatabase {
  dataChange = new BehaviorSubject<CategoryNode[]>([]);
  treeData?: any[];

  get data(): CategoryNode[] {
    return this.dataChange.value;
  }

  constructor(private appService: AppService, transactionTypeEnum: TransactionTypeEnum) {
    this.initialize(transactionTypeEnum);
  }



  initialize(transactionTypeEnum: TransactionTypeEnum) {

    switch (transactionTypeEnum) {
      case TransactionTypeEnum.REVENUE:
        this.appService.sharedCategoryTreeRevenueObservable$.subscribe(tree => {
          this.treeData = tree;
          // Build the tree nodes from Json object. The result is a list of `TodoItemNode` with nested
          //     file node as children.
          const data = tree;

          // Notify the change.
          this.dataChange.next(data);

        });
        break;
      case TransactionTypeEnum.EXPENSES:
        this.appService.sharedCategoryTreeExpensesObservable$.subscribe(tree => {
          this.treeData = tree;
          // Build the tree nodes from Json object. The result is a list of `TodoItemNode` with nested
          //     file node as children.
          const data = tree;

          // Notify the change.
          this.dataChange.next(data);

        });
        break;
      case TransactionTypeEnum.BOTH:


        this.appService.sharedCategoryTreeObservable$.subscribe(tree => {
          this.treeData = tree;
          // Build the tree nodes from Json object. The result is a list of `TodoItemNode` with nested
          //     file node as children.
          const data = tree;

          // Notify the change.
          this.dataChange.next(data);

        });
        break;

    }





  }

  public filter(filterText: string) {
    let filteredTreeData;
    if (filterText && filterText.trim().length > 0) {
      // Filter the tree
      function filter(array: any, text: any) {
        const getChildren = (result: any[], object: { qualifiedName: string; children: any[]; }) => {

          if (object.qualifiedName.toLowerCase() === text.toLowerCase() || object.qualifiedName.toLowerCase().includes(text.toLowerCase())) {
            result.push(object);
            return result;
          }
          if (Array.isArray(object.children)) {
            const children = object.children.reduce(getChildren, []);
            if (children.length) result.push({ ...object, children });
          }
          return result;
        };

        return array.reduce(getChildren, []);
      }

      filteredTreeData = filter(this.treeData, filterText);
    } else {
      // Return the initial tree
      filteredTreeData = this.treeData;
    }

    // Build the tree nodes from Json object. The result is a list of `TodoItemNode` with nested
    // file node as children.
    const data = filteredTreeData;
    // Notify the change.
    this.dataChange.next(data);
  }
}




@Component({
    selector: 'app-category-tree-dropdown',
    templateUrl: './category-tree-dropdown.component.html',
    styleUrls: ['./category-tree-dropdown.component.scss'],
    standalone: true,
    imports: [FormsModule, ReactiveFormsModule, MatFormField, MatInput, MatAutocompleteTrigger, MatAutocomplete, MatOption, MatTree, MatTreeNodeDef, MatTreeNode, MatTreeNodeToggle, MatTreeNodePadding, MatIconButton, MatCheckbox, MatIcon]
})
export class CategoryTreeDropdownComponent implements OnInit  {

  /** Map from flat node to nested node. This helps us finding the nested node to be modified */
  flatNodeMap = new Map<FlatCategoryNode, CategoryNode>();

  /** Map from nested node to flattened node. This helps us to keep the same object for selection */
  nestedNodeMap = new Map<CategoryNode, FlatCategoryNode>();

  qualifiedNameToNodeMap = new Map<string, CategoryNode>();

  @Input()
  selectedCategoryQualifiedNameStr?: string;
  selectedCategory?: CategoryNode;
  selectedCategoryName?: string = "select category"
  // treeControl = new NestedTreeControl<CategoryNode>(node => node.children);
  treeControl!: FlatTreeControl<FlatCategoryNode>
  dataSource!: MatTreeFlatDataSource<CategoryNode, FlatCategoryNode>;
  @Input()
  transactionTypeEnum?: TransactionTypeEnum;

  @Output() selectionChange: EventEmitter<string> = new EventEmitter<string>();

  @ViewChild('formField') formField!: MatFormField;
  @ViewChild('autoCompleteTrigger') autoCompleteTrigger!: MatAutocompleteTrigger;




  treeFlattener: MatTreeFlattener<CategoryNode, FlatCategoryNode>;

  /** The selection for checklist */
  checklistSelection = new SelectionModel<FlatCategoryNode>(false /* multiple */);
  _database?: BackingDatabase;

  constructor(private appService: AppService, private formBuilder: FormBuilder) {
    this.categoryFormGroup = this.formBuilder.group({
      queryForm: "", searchField: ""
    });
    this.treeFlattener = new MatTreeFlattener(
      this.transformer,
      this.getLevel,
      this.isExpandable,
      this.getChildren
    );
    this.treeControl = new FlatTreeControl<FlatCategoryNode>(
      this.getLevel,
      this.isExpandable
    );
    this.dataSource = new MatTreeFlatDataSource(
      this.treeControl,
      this.treeFlattener
    );

  }



  ngOnInit(): void {

    if (this.selectedCategoryQualifiedNameStr !== undefined) {
      this.selectedCategory = this.qualifiedNameToNodeMap.get(this.selectedCategoryQualifiedNameStr);
      if (this.selectedCategory !== undefined) {
        let flatNode = this.nestedNodeMap.get(this.selectedCategory);
        if (flatNode !== undefined) {
          this.toggleSelectionWithoutEmittingChange(flatNode);

        }
      }

    }
    if (this.transactionTypeEnum !== undefined) {
      this._database = new BackingDatabase(this.appService, this.transactionTypeEnum);
      this._database.dataChange.subscribe(data => {
        this.dataSource.data = data;
      });
    }


  }


  transformer = (node: CategoryNode, level: number) => {
    const existingNode = this.nestedNodeMap.get(node);
    const flatNode =
      existingNode && existingNode.qualifiedName === node.qualifiedName
        ? existingNode
        : new FlatCategoryNode();
    flatNode.name = node.name;
    flatNode.nodeId = node.id;
    flatNode.qualifiedName = node.qualifiedName;
    flatNode.level = level;
    flatNode.expandable = (node.children != undefined && node.children.length > 0);
    flatNode.type = node.type;
    this.flatNodeMap.set(flatNode, node);
    this.nestedNodeMap.set(node, flatNode);
    this.qualifiedNameToNodeMap.set(node.qualifiedName, node);
    return flatNode;
  };

  getLevel = (node: FlatCategoryNode) => node.level;

  isExpandable = (node: FlatCategoryNode) => node.expandable;

  getChildren = (node: CategoryNode): CategoryNode[] => node.children;

  hasChild = (_: number, _nodeData: FlatCategoryNode) => _nodeData.expandable;

  hasNoContent = (_: number, _nodeData: FlatCategoryNode) => _nodeData.name === "";
  categoryFormGroup: FormGroup;

  filterChanged(event: Event) {

    let filterText: string = (<HTMLInputElement>(event as InputEvent).target).value
    // ChecklistDatabase.filter method which actually filters the tree and gives back a tree structure
    this._database?.filter(filterText);
    if (filterText) {
      this.treeControl.expandAll();
    } else {
      this.treeControl.collapseAll();
    }
  }

  toggleSelection(node: FlatCategoryNode): void {
    this.checklistSelection.toggle(node);
    if(this.checklistSelection.isSelected(node)){
      this.selectedCategory = this.flatNodeMap.get(node);
      this.selectedCategoryQualifiedNameStr = this.selectedCategory?.qualifiedName;
      this.selectedCategoryName = this.selectedCategory?.name;
      this.selectionChange.emit(this.selectedCategoryQualifiedNameStr);
    } else{
      this.selectedCategory = NO_CATEGORY;
      this.selectedCategoryQualifiedNameStr = this.selectedCategory?.qualifiedName;
      this.selectedCategoryName = "selecteer categorie";
      this.selectionChange.emit(this.selectedCategoryQualifiedNameStr);
    }
    this.categoryFormGroup.controls['searchField'].reset();
    this._database?.filter("")
    this.autoCompleteTrigger.closePanel();

  }
  toggleSelectionWithoutEmittingChange(node: FlatCategoryNode): void {
    this.checklistSelection.toggle(node);
    if(this.checklistSelection.isSelected(node)){
      this.selectedCategory = this.flatNodeMap.get(node);
      this.selectedCategoryQualifiedNameStr = this.selectedCategory?.qualifiedName;
      this.selectedCategoryName = this.selectedCategory?.name;
    } else {
      this.selectedCategory = NO_CATEGORY;
      this.selectedCategoryQualifiedNameStr = this.selectedCategory?.qualifiedName;
      this.selectedCategoryName = "selecteer categorie";
    }

  }









}

import {FormGroup} from "@angular/forms";

import {CategoryType} from "../model";
import {
  RuleMatchType,
  Rule as ClientRule,
  RuleOperator,
  RuleSet as ClientRuleSet,
  RuleSetRulesInner as ClientRuleSetRulesInner,
  RuleSetWrapper as ClientRuleSetWrapper,
  FieldTypeEnum,
  ConditionEnum,
  TypeEnum,
  SimpleCategory,
  SimpleUser
} from "@daanvdn/budget-assistant-client";

// Extended type to ensure all required properties exist on both Rule and RuleSet
// Using any as a temporary solution to get the code to compile
export type RuleSetRulesInner = any;


// Using FieldTypeEnum from @daanvdn/budget-assistant-client
// export type FieldType = 'string' | 'number' | 'categorical' | 'null';
export type FieldType = FieldTypeEnum | 'null';

// Extended type to ensure string is a valid value for ConditionEnum
export type ExtendedConditionEnum = ConditionEnum;

// Define MatchType for compatibility with RuleMatchType
export type MatchType = RuleMatchType;


export function objectsAreEqual(obj1: any, obj2: any): boolean {
  // Get the keys
  const keys1 = Object.keys(obj1);
  const keys2 = Object.keys(obj2);

  // If number of keys is different,
  // then objects are not equal
  if (keys1.length !== keys2.length) {
    return false;
  }

  // Iterate over keys
  for (const key of keys1) {
    const val1 = obj1[key];
    const val2 = obj2[key];
    const areObjects = isObject(val1) && isObject(val2);
    if ((areObjects && !objectsAreEqual(val1, val2)) || (!areObjects && val1 !== val2)) {
      return false;
    }
  }

  return true;
}

export function isObject(object: any): boolean {
  return object != null && typeof object === 'object';
}

// Using RuleOperator from @daanvdn/budget-assistant-client
export class Operator implements RuleOperator {
  readonly name: string;
  readonly value: string;
  readonly type: string;

  constructor(name: string, value: string, fieldType: FieldType) {
    this.value = value;
    this.type = fieldType;
    this.name = name;
  }

  equals(operator: Operator): boolean {
    return this.value === operator.value && this.type === operator.type && this.name === operator.name;
  }
  public asOperator():  RuleOperator {
   return this;
}
}


export class StringOperators {

  static CONTAINS = new Operator('contains', 'contains', 'string');
  static EXACT_MATCH = new Operator('exact match', 'exact match', 'string');
  static FUZZY_MATCH = new Operator('fuzzy match', 'fuzzy match', 'string');
  static ALL: Operator[] = [StringOperators.CONTAINS, StringOperators.EXACT_MATCH, StringOperators.FUZZY_MATCH];
}

export class CategoricalOperators {
  static IN = new Operator('in', 'in', 'categorical');
  static NOT_IN = new Operator('not in', 'not in', 'categorical');
  static EQUALS = new Operator('equals', 'equals', 'categorical');
  static ALL: Operator[] = [CategoricalOperators.IN, CategoricalOperators.NOT_IN, CategoricalOperators.EQUALS];
}

export class NumericalOperators {
  static EQUALS = new Operator('equals', '=', 'number');
  static NOT_EQUALS = new Operator('not equals', '!=', 'number');
  static GREATER_THAN = new Operator('greater than', '>', 'number');
  static GREATER_THAN_OR_EQUALS = new Operator('greater than or equals', '>=', 'number');
  static LESS_THAN = new Operator('less than', '<', 'number');
  static LESS_THAN_OR_EQUALS = new Operator('less than or equals', '<=', 'number');
  static ALL: Operator[] = [NumericalOperators.EQUALS, NumericalOperators.NOT_EQUALS, NumericalOperators.GREATER_THAN, NumericalOperators.GREATER_THAN_OR_EQUALS, NumericalOperators.LESS_THAN, NumericalOperators.LESS_THAN_OR_EQUALS];
}


function isEmptyString(value: string | undefined | null): boolean {
  return !value || value === 'null' || value === 'undefined' || value.trim().length === 0;

}


// Using ClientRuleSet from @daanvdn/budget-assistant-client
export class RuleSet implements ClientRuleSet {
  clazz: string = 'RuleSet';
  type: any; // Required by ClientRuleSet
  condition: ExtendedConditionEnum;
  rules: Array<RuleSetRulesInner> = [];
  isChild: boolean = false;

  // Additional properties not in ClientRuleSet
  collapsed?: boolean;

  constructor(condition: string, rules: Array<any>, collapsed?: boolean, isChild?: boolean) {
    this.clazz = 'RuleSet';
    this.type = 'RuleSet'; // Required by ClientRuleSet
    this.condition = condition as ConditionEnum;

    // Convert rules to RuleSetRulesInner array
    if (rules) {
      this.rules = rules as Array<RuleSetRulesInner>;
    }

    this.collapsed = collapsed;
    this.isChild = isChild ?? false;
  }

  public clone(): RuleSet {
    let clone: RuleSet = {...this};
    delete clone.collapsed;
    clone.isChild = false;

    clone.rules = clone.rules.map((rule: any) => {
      if (rule instanceof RuleSet) {
        return rule.clone();
      } else if (rule instanceof Rule) {
        return rule.clone();
      }
      return rule;
    });
    return clone;
  }

  public isComplete(): boolean {
    if (isEmptyString(this.condition)) {
      return false;
    }

    if (this.rules.length === 0) {
      return false;
    }

    for (const item of this.rules) {
      if ((item as any) instanceof RuleSet) {
        if (!(item as RuleSet).isComplete()) {
          return false;
        }
      } else if ((item as any) instanceof Rule) {
        if (!(item as Rule).isComplete()) {
          return false;
        }
      }
    }

    return true;
  }

  public toJson(): string {
    if (!this.isComplete()) {
      throw new Error('RuleSet is not complete');
    }

    let clone: RuleSet = this.clone();

    /*
        // Post-process the serialized JSON to remove properties that are undefined
        return JSON.parse(JSON.stringify(json, (key, value) => value === undefined ? undefined : value));
    */

    let mapper = (key: string, value: any) => {
      if (key === 'field' && value instanceof Field) {
        return (value as Field).pathFromTransaction;
      }
      if (key === 'field' && value instanceof Array) {
        return value.map((f: Field) => (f as Field).pathFromTransaction);
      }
      if (value && typeof value === 'object' && 'name' in value && 'value' in value) {
        return value.name;
      }

      return value;
    }

    return JSON.stringify(clone, mapper);
  }
}


