function createMatchTypesByNameMap(MATCH_TYPES: RuleMatchType[]): Map<string, RuleMatchType> {
  let map = new Map<string, RuleMatchType>();
  for (let matchType of MATCH_TYPES) {
    map.set(matchType.name, matchType);
  }
