    if (!this.field || this.field.length === 0) {
      return false;
    }

    if (isEmptyString(this.fieldType)) {
      return false;
    }

    if (this.isEmptyOperator(this.operator)) {
      return false;
    }

    if (!this.value || this.value.length === 0) {
      return false;
    }

    if (this.valueMatchType == undefined) {
