    }

    function isUndefinedOrEmpty(value: any): boolean {
      return value === undefined || value === null || (Array.isArray(
        value) && (value as Array<any>).length == 0) || value === '';
    }

    if (isUndefinedOrEmpty(rule.fieldType)) {
      return false;
