  static serializeRuleSet(ruleSet: RuleSet): string {
    const cache = new Set();
    const jsonString = JSON.stringify(ruleSet, (key, value) => {
      if (typeof value === 'object' && value !== null) {
        if (cache.has(value)) {
          // Circular reference found, discard key
          return;
